mapper.update:
UPDATE board_shop_review
SET deleted = 'yes'
WHERE num = #{num}

UPDATE board_shop_review
SET content = #{content}
WHERE num = #{num}

mapper.select:
SELECT num, writer, content, ref_group, review_group, deleted, TO_CHAR(regdate, 'YY.MM.DD HH24:MI') AS regdate
FROM board_shop_review
WHERE num = #{num}

SELECT NVL(MAX(ROWNUM), 0)
FROM board_shop_review
WHERE ref_group = #{ref_group}
